SELECT * FROM biz_teacher_commend_penalize btcp



        AND btcp.TITLE LIKE #{vtitle}


        AND btcp.DATE = #{date}


        AND btcp.TEACHER_ID = #{teacherId}



        AND btcp.TEACHER_NAME LIKE #{vteacherName}


        AND btcp.STATUS = #{status}


        AND btcp.LEVEL = #{level}


        AND btcp.TYPE = #{type}


    ORDER BY btcp.CREATE_TIME DESC
